import { ReactNode, CSSProperties, useState } from "react";
import ToDoListItem from "../ToDoListItem";

type Todo = {
  id: number;
  text: string;
  checked: boolean;
};

export default function ToDoList() {
  const [todoList, setTodoList] = useState<Todo[]>([
    {
      id: 1,
      text: "First Todo",
      checked: false,
    },
    {
      id: 2,
      text: "Second Todo",
      checked: true,
    },
  ]);
  return (
    <ToDoListItem
      type={"checkbox"}
      items={todoList.map((todo) => (
        <ToDoListItem
          key={todo.id}
          type="checkbox"
          checked={todo.checked}
          onChecked={(checked) => {
        const updatedList = todoList.map((item) =>
          item.id === todo.id ? { ...item, checked } : item
        );
        setTodoList(updatedList);
          }}
        >
          {todo.text}
        </ToDoListItem>
      ))}
      checked={false}
      onChecked={(checked) => {
        console.log(checked);
      }}
    />
  );
}
