import { ReactNode, CSSProperties, ChangeEvent } from "react";

type Props = {
  type: string;
  placeholder?: string;
  value: string;
  onChange: (e: ChangeEvent<HTMLInputElement>) => void;
  style?: CSSProperties;
  onBlur: () => void;
};

export default function Input({
  type,
  placeholder,
  value,
  onChange,
  style,
}: Props) {
  return (
    <input
      type={type}
      placeholder={placeholder}
      value={value}
      onChange={onChange}
      style={{
        color: "blue",
        backgroundColor: "lightblue",
        fontSize: 12,
        padding: "8px",
        boxSizing: "border-box",
        ...style,
      }}
    />
  );
}
