import { ReactNode, CSSProperties } from "react";

type Props = {
  children: ReactNode;
  style?: CSSProperties;
};

export default function Input({ children, style }: Props) {
  return (
    <input
      style={{
        color: "blue",
        backgroundColor: "lightblue",
        fontSize: 12,
        padding: "8px",
        boxSizing: "border-box",
        ...style,
      }}
    >
      {children}
    </input>
  );
}
