import React, { useState } from "react";
import Button from "../Button";

type Props = {
    isOpen: boolean;
    onClose: () => void;
    children: React.ReactNode;
};

export const Modal = ({ isOpen, onClose }: Props) => {
    const [isEditing, setIsEditing] = useState(false);
    const [text, setText] = useState("Click to edit");

    if (!isOpen) return null;

    const handleSave = () => {
        setIsEditing(false);
    };

    const handleTextClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setText(e.target.value);
    };

    const handleInputBlur = () => {
        setIsEditing(false);
    };

    return (
        <div
            style={styles.overlay}
            onClick={onClose}
        >
            <div
                style={styles.modal}
                onClick={(e) => e.stopPropagation()}
            >
                <button
                    style={styles.closeButton}
                    onClick={onClose}
                >
                    âœ–
                </button>
                {isEditing ? (
                    <input
                        type="text"
                        value={text}
                        onChange={handleInputChange}
                        onBlur={handleInputBlur}
                        autoFocus
                        style={styles.input}
                    />
                ) : (
                    <p
                        onClick={handleTextClick}
                        style={styles.text}
                    >
                        {text}
                    </p>
                )}
                <Button
                    style={styles.saveButton}
                    onClick={handleSave}
                >
                    Save
                </Button>
            </div>
        </div>
    );
};

const styles = {
    overlay: {
        position: "fixed" as "fixed",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "rgba(0, 0, 0, 0.5)",
    },
    modal: {
        backgroundColor: "white",
        padding: "20px",
        borderRadius: "8px",
        boxShadow: "0px 4px 6px rgba(0, 0, 0, 0.1)",
        width: "400px",
        position: "relative" as "relative",
    },
    closeButton: {
        position: "absolute" as "absolute",
        top: "10px",
        right: "10px",
        background: "none",
        border: "none",
        fontSize: "16px",
        cursor: "pointer",
        color: "gray",
    },
    input: {
        width: "100%",
        padding: "8px",
        fontSize: "16px",
    },
    text: {
        cursor: "pointer",
        fontSize: "16px",
    },
    saveButton: {
        marginTop: "10px",
        padding: "10px 20px",
        fontSize: "16px",
        cursor: "pointer",
        backgroundColor: "#007BFF",
        color: "white",
        border: "none",
        borderRadius: "4px",
    },
};
